import { handleActions } from 'redux-actions';
import actionTypes from './actionTypes';
import StoreState from './storeState';

// 初始化redux数据
const initialState: StoreState = {
    enthusiasmLevel: 2,
    languageName: 'react + redux + react-router-v4 + typescript + antd + scss + ^es6:',
};

// actions
export default handleActions({
    [actionTypes.INCREMENT_ENTHUSIASM]: (state, action) => {
        return {
            ...state,
            enthusiasmLevel: state.enthusiasmLevel + 1,
        };
    },
    [actionTypes.DECREMENT_ENTHUSIASM]: (state, action) => {
        return {
            ...state,
            enthusiasmLevel: Math.max(1, state.enthusiasmLevel - 1),
        };
    },
    [actionTypes.SET_NEW_MSG]: (state, action) => {
        return {
            ...state,
            languageName: action.payload.languageName,
        };
    },
    
}, initialState);